int bufferSizeInBytes = 8192; // 8 KB
byte[] buffer = new byte[bufferSizeInBytes];
for (int i = 0; i < bufferSizeInBytes; i++)
{
    buffer[i] = (byte)'A'; // 8 KB boyunca 'A' karakterini ekler
}

string eightKBString = Encoding.UTF8.GetString(buffer);

Console.WriteLine("8 KB boyutunda bir string oluşturuldu.");
Console.WriteLine($"String uzunluğu: {eightKBString.Length} karakter");

Console.WriteLine(eightKBString);
